.productView-description .tabs {
  position: relative;
  margin: 3rem 0;
  background: transparent;
}
.productView-description .tabs::before,
.productView-description .tabs::after {
  content: "";
  display: table;
}
.productView-description .tabs::after {
  clear: both;
}
.productView-description .tab {
  float: left;
  position: initial;
}
.productView-description .tab-switch {
  display: none;
}
.productView-description .tab-label {
  position: relative;
  display: block;
  line-height: 2.75em;
  height: 3em;
  padding: 0 1.618em;
  background: transparent;
  border-right: 0;
  color: grey;
  cursor: pointer;
  top: 0;
  transition: all 0.25s;
}
.productView-description .tab-label:hover {
  top: -0.25rem;
  transition: top 0.25s;
}
.productView-description .tab-content {
  position: absolute;
  z-index: 1;
  top: 2.75em;
  left: 0;
  padding: 1.618rem;
  background: #fff;
  color: #2c3e50;
  opacity: 0;
  transition: all 0.35s;
  display: block;
}
.productView-description .tab-switch:checked + .tab-label {
  background: #fff;
  color: #00f;
  border-bottom: 0; 
  border-right: 0;
  transition: all 0.35s;
  z-index: 1;
  top: -0.0625rem;
}
.productView-description .tab-switch:checked + label + .tab-content {
  z-index: 2;
  opacity: 1;
  transition: all 0.35s;
}


