.previewCartCheckout{
.button{
  background-color: stencilColor("button--default-backgroundColor");
  color: stencilColor("button--default-color");
  border: 1px solid stencilColor("button--default-borderColor") !important;
  &:hover{
    background-color: stencilColor("button--default-backgroundColorHover");
    color: stencilColor("button--default-colorHover");
    border-color: stencilColor("button--default-borderColorHover") !important;
  }
  &:active{
    background-color: stencilColor("button--default-backgroundColorActive");
    color: stencilColor("button--default-colorActive");
    border-color: stencilColor("button--default-borderColorActive") !important;
  }
}

.button--primary{
  background-color: stencilColor("button--primary-backgroundColor") !important;
  color: stencilColor("button--primary-color");
  border: 1px solid stencilColor("button--primary-backgroundColor") !important;
  &:hover{
    background-color: stencilColor("button--primary-backgroundColorHover") !important;
    color: stencilColor("button--primary-colorHover");
    border-color: stencilColor("button--primary-backgroundColorHover") !important;
  }
  &:active{
    background-color: stencilColor("button--primary-backgroundColorActive") !important;
    color: stencilColor("button--primary-colorActive");
    border-color: stencilColor("button--primary-backgroundColorActive") !important;
  }
}
}

.writeReview-productImage-container {
margin-bottom: 20px;
  img {
      bottom: 0;
      left: 0;
      margin: auto;
      position: absolute;
      right: 0;
      top: 0;
      font-family: "object-fit: contain;";
      height: 100%;
      -o-object-fit: contain;
      object-fit: contain;
  }
}

.productView-options {
.form-action{
  .button--primary{
    background-color: stencilColor("button--primary-backgroundColor") !important;
    color: stencilColor("button--primary-color");
    border: 1px solid stencilColor("button--primary-backgroundColor") !important;
    &:hover{
      background-color: stencilColor("button--primary-backgroundColorHover") !important;
      color: stencilColor("button--primary-colorHover");
      border-color: stencilColor("button--primary-backgroundColorHover") !important;
    }
    &:active{
      background-color: stencilColor("button--primary-backgroundColorActive") !important;
      color: stencilColor("button--primary-colorActive");
      border-color: stencilColor("button--primary-backgroundColorActive") !important;
    }
  }
}
}

.productView-reviewLink > a{
color: stencilColor("color-textLink");
&:hover{
  color: stencilColor("color-textLink--hover");
}
&:active{
  color: stencilColor("color-textLink--active");
}
}

