<footer class="footer" role="contentinfo">
    <div class="container">
        <section class="footer-info">
            <article class="footer-info-col footer-info-col--small-4" data-section-type="footer-webPages">
                <h5 class="footer-info-heading">{{lang 'footer.navigate'}}</h5>
                <ul class="footer-info-list">
                    {{#each pages}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    {{#if settings.account_creation_enabled}}
                        {{#if customer}}
                            <li><a class="" href="{{urls.account.index}}">{{lang 'common.account'}}</a></li>
                            <li><a class="" href="{{urls.auth.logout}}">{{lang 'common.logout'}}</a></li>
                        {{else}}
                            <li><a class="" href="{{urls.auth.login}}">{{lang 'common.login'}}  </a>
                            {{#if settings.account_creation_enabled}}
                                <span class="navUser-or"> {{ lang 'common.or' }} </span> <a class="" href="{{urls.auth.create_account}}">  {{lang 'common.sign_up'}}</a></li>
                            {{/if}}
                        {{/if}}
                    {{/if}}
                    <li>
                        <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                    </li>
                </ul>
            </article>

            <article class="footer-info-col footer-info-col--small-4 categories" data-section-type="footer-categories">
                <h5 class="footer-info-heading">{{lang 'footer.categories'}}</h5>
                <ul class="footer-info-list categories">
                    {{#each categories}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <div class="footer-categories-pagination">
                      <a class="navPages-action paginator previous arrow">
                        <i class="icon prev" aria-hidden="true">
                          <svg><use xlink:href="#icon-chevron-left" /></svg>
                        </i>
                        <span class="pagination-label">prev</span>
                      </a>
                      <span class="dots"></span>
                      <a class="navPages-action paginator next arrow">
                        <span class="pagination-label">next</span>
                        <i class="icon next" aria-hidden="true">
                          <svg><use xlink:href="#icon-chevron-left" /></svg>
                        </i>
                      </a>
                    </div>
                </ul>
            </article>

            {{#and theme_settings.shop_by_brand_show_footer shop_by_brand.length}}
            <article class="footer-info-col footer-info-col--small-4" data-section-type="footer-brands">
                <h5 class="footer-info-heading">{{lang 'footer.brands'}}</h5>
                <ul class="footer-info-list">
                    {{#each shop_by_brand}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                </ul>
            </article>
            {{/and}}
            
            
            {{#if theme_settings.show_social_icons '===' true}}
              <article class="footer-info-col footer-info-col--small-4" data-section-type="footer-webPages">
                <h5 class="footer-info-heading">{{lang 'social.connect'}}</h5>
                  {{> components/common/social-links}}
                  {{#if settings.phone_number}}
                    <!-- <li class="navPages-item navPages-item-page pages phone-number"> -->
                      <a class="navPages-action phone-number">{{settings.phone_number}}</a>
                    <!-- </li> -->
                  {{/if}}
                  {{{region name="ssl_site_seal--global"}}}
              </article>
            {{/if}}
            
            <article class="footer-info-col footer-info-col--small-4 newsletter cta" data-section-type="newsletterSubscription">
                <h5 class="footer-info-heading">Newsletter</h5>
               {{#if settings.show_newsletter_box}}
                 {{> components/common/subscription-form}} 
               {{/if}}
            </article>
    </div>

   

    <div class="footer-bottom">
      {{#if settings.address}}
      <article class="store-info" data-section-type="storeInfo">
          <address>{{settings.address}}</address>
          {{#if settings.phone_number}}
              {{settings.phone_number}}
          {{/if}}
      </article>
      {{/if}}

      {{#if theme_settings.show_powered_by}}
        <div class="footer-copyright">
            <p class="powered-by text">Powered by <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow">BigCommerce</a></p>
        </div>
      {{/if}}
      {{#if theme_settings.show_created_by}}
        <div class="footer-copyright">
          <p class="powered-by created-by text">Created by <a href="https://lonestartemplates.com/" rel="nofollow">Lone Star Templates</a></p>
        </div>
      {{/if}}

      {{#if theme_settings.show_copyright_footer}}
        <div class="footer-copyright">
            <p class="powered-by text">&copy; {{moment format="YYYY"}} {{settings.store_name}} </p>
        </div>
      {{/if}}
    </div> <!-- footer-bottom -->

    <div id="topcontrol" class="backtoTop" title="Scroll Back to Top">
      <button class="slick-prev slick-arrow" aria-label="Previous" type="button" style="">Previous</button>
      <div class="clear"></div>
    </div>

</footer>

